<?phpinclude_once( './httpful.phar' ); # This loads the Httpful client#get necessary variables to access the API$schoolWebsite = "**YOUR SCHOOL WEBSITE**"; # website used to login to the "ON" products$apiUser = "**YOUR API USERNAME**";$apiPassword = "**YOUR API PASSWORD***";// Start with no error$error = false;# Authenticate to the API, and get a token to use for further calls$uri = "$schoolWebsite/api/authentication/login/?username=$apiUser&password=$apiPassword&format=json";try {	$response = \Httpful\ Request::get( $uri )->expectsJson()->send();	# Store the token as a variable so it's handy	$token = $response->body->{"Token"};	# Call the list API	$uri = $schoolWebsite . "/api/list/***YOUR LIST ID***/?t=" . $token . "&format=json";	$response = \Httpful\ Request::get( $uri )->send();	$listResults = $response->body;	/* The following would just dump all the results to the document.window.	var_dump($listResults);	*/	/* The following would take the results, make it pretty and either begin a new file or append to exisiting file data.json	$file = "data.json";	$json_string = json_encode($listResults,JSON_PRETTY_PRINT);	file_put_contents($file,$json_string,FILE_APPEND);	*/} catch ( Exception $e ) {	$error = 'The database is down. Please check back later.';	//	echo "<pre>";	//	print_r($e);	//	echo "</pre>";}?><!doctype html><html><head>	<meta charset="UTF-8">	<title>SHP Upcoming Games</title>	<style>		.data-display {			margin-bottom: 50px;			padding: 15px;			font-family: 'Montserrat', 'sans-serif';		}				.game {			padding: 15px 15px;			margin: 0 -15px;		}				.game:nth-child(odd) {			background: #eee;		}				h2 {			font-family: 'Montserrat', sans-serif;			font-size: 1.6em;			text-transform: uppercase;			letter-spacing: .05em;			margin: 0 0 15px 0;		}				h4 {			font-family: 'Montserrat', sans-serif;			font-size: 1.2em;			text-transform: uppercase;			letter-spacing: .05em;			font-weight: normal;			margin: 0;		}				p {			font-family: 'Montserrat', sans-serif;			font-size: 1em;			font-weight: normal;			margin: 0;		}				.daytime {			font-family: 'Montserrat', sans-serif;			font-size: 1.0em;			margin-bottom: 10px;		}				.dismissaltime {			font-family: 'Montserrat', sans-serif;			font-size: .8em;		}				.teamtitle {			font-family: 'Montserrat', sans-serif;			font-size: 1.0em;			text-transform: uppercase;			letter-spacing: .05em;			font-weight: normal;		}				a {			color: #930123;		}				.location {			margin-top: 10px;		}	</style></head><body>	<h2>TITLE</h2>		<?php	if ( $error ) {		echo $error;	} else {		/* The following would display events happening today or later as compared to Today's date */		$todaysDate = new DateTime();		$i = 0;		echo "<div class='data-display'>	<div class='athleticupcominggames'>";		foreach ( $listResults as $listResult ) {			$i++;			$fname = $listResult->{"SportLevelTitle"};			$fdate = $listResult->{"GameDate"};			$ftime = $listResult->{"GameTime"};			$ftitle = $listResult->{"Title"};			$fdismissal = $listResult->{"StudentDismissalTime"};			$fopponent = $listResult->{"Name"};			$flocation = $listResult->{"Location"};			// Don't need location and zip for gmaps. Gmaps seems fine without it			//	$flocationaddress = $listResult->{"LineOne"};			$flocationzip = $listResult->{"Zip"};			// Echo results with styling			echo "<div class='game'><div class='daytime'>";			echo date_format( new Datetime( $fdate ), 'l, F j, Y' );			echo "    $ftime</div>";			echo "<div class='teamtitle'>$fname</div>";			echo "vs. $fopponent<br>";			if ( !empty( $fdismissal ) ) {				echo "<div class='dismissaltime'>Early Dismissal Time: $fdismissal</div>";			}            echo "<div>$ftitle</div>";			// use name of school & zip for gmaps			// don't need address			//		echo "<a href='https://www.google.com/maps/place/+$flocationaddress+$flocationzip' target='_blank'>$flocation</a>";			echo "<div class='location'><a href='https://www.google.com/maps/place/+$flocation+$flocationzip' target='_blank'>$flocation</a>";			echo "<br>\n";			echo "</div></div>";		}		echo "</div></div>";	}	?></body></html>